  class HasType[T](implicit t: c.WeakTypeTag[T]) {

    def matches(tree: Tree) = {
      c.Expr(c.typecheck(tree, silent = true)).actualType =:= t.tpe ||
        c.Expr(c.typecheck(tree, mode = c.TYPEmode, silent = true)).actualType =:= t.tpe
    }

    def unapply(tree: Tree): Option[Tree] = {
      if (matches(tree)) Some(tree)
      else None
    }
  }
 
  val hasNodeBufferType = new HasType[NodeBuffer]
  val scalaXmlNull = new HasType[scala.xml.Null.type]
  val hasMetaDataType = new HasType[MetaData]
  val hasElemType = new HasType[Elem]
  val hasTextType = new HasType[Text]
  val hasStringType = new HasType[String]
  val hasAttributeType = new HasType[UnprefixedAttribute]
  val catalog = scala.collection.mutable.Map[String, String]()

  def isWhitespace(str: String) = new Regex("""^\s*$""", "m").unapplySeq(str).isDefined

  object ElemTransformer extends Transformer {
    var dictionnaireEnCours = scala.collection.mutable.Map[Tree, Tree]()
    val suppressNullChildren = true

    def createElement(instanceDeModule: Tree, param: Tree, children: List[Tree]): Tree = {

      val childrenWithFilter = children.filter(tree => !tree.isEmpty && tree.toString != "\"\"" && tree != q"null")
      if (suppressNullChildren) {
        if (childrenWithFilter.size > 0) q"React.createElement($instanceDeModule , $param, ..$childrenWithFilter )"
        else q"React.createElement($instanceDeModule , $param)"
      } else { q"React.createElement($instanceDeModule , $param , ..$children )" }
    }

    def createElement(instanceDeModule: Tree, param: Tree): Tree = {
      q"React.createElement($instanceDeModule , $param )"
    }

    def createElement(instanceDeModule: TermName, param: Tree, children: List[Tree]): Tree = {
      val childrenWithFilter = children.filter(tree => !tree.isEmpty && tree != q"" && tree != q"null" && tree.toString != "\"\"")
      if (suppressNullChildren) {
        if (childrenWithFilter.size > 0) q"React.createElement($instanceDeModule , $param, ..$childrenWithFilter )"
        else q"React.createElement($instanceDeModule , $param)"
      } else { q"React.createElement($instanceDeModule , $param , ..$children )" }
    }

    def createElement(instanceDeModule: TermName, param: Tree): Tree = {
      q"React.createElement($instanceDeModule , $param )"
    }

    override def transform(tree: Tree): Tree = {
      tree match {

        case q"$mods val $name: $tpt = $tpe" if hasNodeBufferType.matches(tpe) =>
     //    val class1= c.mirror.staticClass("")
        
          q""

        // var $md: _root_.scala.xml.MetaData = _root_.scala.xml.Null
        case q"$mods var $name: $tpt = $tpe" if scalaXmlNull.matches(tpe) =>
          writer.write(s"cas1  traitementExpression = $tree" + "\r\n")

          dictionnaireEnCours.clear
          q""

        case x @ Select(_, _) if scalaXmlNull.matches(x) =>
          writer.write(s"cas3  traitementExpression = $tree" + "\r\n")
          dictionnaireEnCours.clear
          q""

        // traitement du cas $buf.$amp$plus({
        case q"$variable.$params({$listExpr})" if variable.toString().trim == "$buf" =>
          writer.write(s"cas4  traitementExpression = $tree" + "\r\n")
          val traitementExpression = transform(listExpr) //.filter(expr=>expr != q"""null""") //listExpr.map(expr => transform(expr)).filter(x => x != q"")
          val retour = q"..$traitementExpression"
          writer.write(s"cas8  traitementExpression params=$params tree = $tree    retour = $retour" + "\r\n")
